

//quando o form de pesquisa for enviado por submit
$('#formPesquisa').on('submit',function(event){
    event.preventDefault(); //para evitar que a tela recarregue
    var dados = $(this).serialize(); //serialize - envia todos os dados do formul√°rio
    
    $.ajax({
        url: 'controller/controllerPacientes.php',
        type: 'post',
        dataType: 'json',
        data: dados,
        success: function(response){
            //.resultadoForm class table em pages/pacientes.php

            $('.resultadoForm table tbody').empty(); //limpa a cada pesquisa
            $.each(response,function(key,value){                
                $('.resultadoForm table tbody').append("<tr> <td>" + value.id_paciente + "</td> <td>" + value.nome + "</td> <td>" + value.numero + "</td> <td>" + value.tipo_exame + "</td> <td>" + value.modalidade + "</td> <td>" + value.data + "</td> <td><a class='visualizacao' href='#'>"+ value.visualizacao + "</a></td></tr> ");
                
            });
        }
    });
} );

